import React, { useState } from 'react';

const Carousel = () => {
  const slides = [
    { id: 1, image: 'https://via.placeholder.com/800x400', text: 'Slide 1' },
    { id: 2, image: 'https://via.placeholder.com/800x400', text: 'Slide 2' },
    { id: 3, image: 'https://via.placeholder.com/800x400', text: 'Slide 3' },
    { id: 4, image: 'https://via.placeholder.com/800x400', text: 'Slide 4' },
  ];

  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <div className="relative w-full max-w-4xl mx-auto">
      <div className="overflow-hidden rounded-lg">
        <div
          className="flex transition-transform duration-500"
          style={{
            transform: `translateX(-${currentSlide * 100}%)`,
          }}
        >
          {slides.map((slide) => (
            <div
              key={slide.id}
              className="w-full flex-shrink-0"
              style={{ width: '100%' }}
            >
              <img
                src={slide.image}
                alt={slide.text}
                className="w-full h-auto"
              />
              <div className="absolute bottom-5 left-5 text-white bg-black bg-opacity-50 px-4 py-2 rounded">
                {slide.text}
              </div>
            </div>
          ))}
        </div>
      </div>
      {/* Botões de Navegação */}
      <button
        onClick={prevSlide}
        className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75"
      >
        &lt;
      </button>
      <button
        onClick={nextSlide}
        className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75"
      >
        &gt;
      </button>
    </div>
  );
};

export default Carousel;
